            if (!daeGeometry.mesh || !daeGeometry.mesh.primitives || !daeGeometry.mesh.primitives.length) {
              continue;
            }

            var target = daeGeometry.mesh.primitives[0].geometry;

            if (target.vertices.length === geometry.vertices.length) {

